smartCalendar.usageExample:
// Запланувати завдання з Розумним Календарем
smartCalendar.scheduleTask({
  title: 'Завершити проектну пропозицію',
  duration: '2 години',
  priority: 'високий'
});

taskManagement.usageExample:
// Створити нове завдання з підзавданнями
const projectTask = taskManager.createTask({
  title: 'Запуск нового продукту',
  dueDate: '2023-12-31',
  subtasks: [
    { title: 'Завершити дизайн', assignee: 'Аліса' },
    { title: 'Написати документацію', assignee: 'Богдан' }
  ]
});

aiAssistant.usageExample:
// Отримати пропозиції завдань на основі ШІ
const suggestions = await aiAssistant.getSuggestions({
  user: currentUser,
  timeFrame: 'сьогодні'
});

suggestions.forEach(suggestion => {
  console.log(`Пропозиція: ${suggestion.text}`);
});

teamCollaboration.usageExample:
// Поділитися завданням з членами команди
const sharedTask = await taskManager.shareTask({
  taskId: '123456',
  sharedWith: ['team@example.com'],
  permissions: ['перегляд', 'редагування']
});

console.log(`Завдання поділено з ${sharedTask.sharedWith.length} членами команди`);

quickActions.usageExample:
// Зареєструвати користувацьку швидку дію
quickActions.register({
  name: 'Створити термінове завдання',
  shortcut: 'Ctrl+Shift+U',
  action: () => {
    taskManager.createTask({ title: 'Термінове завдання', priority: 'високий' });
  }
});

smartNotifications.usageExample:
// Налаштувати розумне сповіщення
smartNotifications.create({
  taskId: '789012',
  message: 'Не забудьте підготуватися до завтрашньої зустрічі',
  smartTiming: true,
  channels: ['push', 'email']
});
